import React, { Component } from 'react';

class Dog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: ''
    };

    this.fetchDog = this.fetchDog.bind(this);
  }

  componentDidMount() {
    this.fetchDog();
  }

  fetchDog() {
    fetch('https://dog.ceo/api/breeds/image/random')
      .then((response) => response.json())
      .then((result) => { this.setState({ data: result }); });
  }

  render() {
    const { dog, storageDog } = this.state;
    console.log(storageDog);
    console.log(dog);
    return (
      <div>
        <p>Doguinhos</p>
        <button onclick={this.fetchDog}>New Doguito</button>
      </div>
    );
  }
}

export default Dog;
