import React, { Component } from 'react';
import './Dog.css';

class Dog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],
    };

    this.fetchDog = this.fetchDog.bind(this);
  }

  componentDidMount() {
    this.fetchDog();
  }

  fetchDog() {
    const { data } = this.state;
    fetch('https://dog.ceo/api/breeds/image/random')
      .then((response) => response.json())
      // .then((result) => { this.setState({ data: result }); });
      .then((result) => { this.setState({ data: [...data, result] }); });

    /* this.setState(({ data }) => ({data: [...data, result] })) */
  }

  render() {
    const { data } = this.state;
    if (data === null) {
      return (
        <h1>Loading...</h1>
      );
    }
    return (
      <div>
        <p>Doguinhos</p>
        <button type="button" onClick={ this.fetchDog }>New Dog</button>
        <div>
          <img src={ data.map((dog) => dog.message) } alt="Doguinho" />
          <div>
            { data.map((dog) => (<img
              src={ dog.message }
              alt="Doguinho"
              key={ dog.message }
            />)) }
          </div>
        </div>
      </div>
    );
  }
}

export default Dog;
