import React, { Component } from 'react';

class Dog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],
    };

    this.fetchDog = this.fetchDog.bind(this);
  }

  componentDidMount() {
    this.fetchDog();
  }

  fetchDog() {
    fetch('https://dog.ceo/api/breeds/image/random')
      .then((response) => response.json())
      .then((result) => { this.setState(({ data }) => ({ data: [...data, result] })); });

    /* this.setState(({ data }) => ({data: [...data, result] })) */
  }

  render() {
    const { data } = this.state;
    if (data === null) {
      return (
        <h1>Loading...</h1>
      );
    }
    return (
      <div>
        <p>Doguinhos</p>
        <button type="button" onClick={ this.fetchDog }>New Dog</button>
        <div>
          <img src={ data.message } alt="Doguinho" />
        </div>
      </div>
    );
  }
}

export default Dog;
