import React, { Component } from 'react';

class Dog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      dog: undefined,
      storageDog: [],
    };
  }

  componentDidMount() {
    this.fetchDog();
  }

  async fetchDog() {
    const requestReturn = await fetch('https://dog.ceo/api/breeds/image/random');
    const requestObj = await requestReturn.json();
    this.setState({
      dog: requestObj,
    });
  }

  render() {
    const { dog, storageDog } = this.state;
    console.log(storageDog);
    return (
      <div>
        { storageDog.map(({ message }) => (<span key={ message }>{ message }</span>)) }
      </div>
    );
  }
}

export default Dog;
