import React, { Component } from 'react';

class Dog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      dog: undefined,
      storageDog: [],
    };
  }

  componentDidMount() {
    this.fetchDog();
  }

  fetchDog = async () => {
    const requestReturn = await fetch('https://dog.ceo/api/breeds/image/random');
    const requestObj = await requestReturn.json();
    this.setState({
      dog: requestObj,
    });
  }

