  render() {
    const { dog, storageDog } = this.state;
    console.log(storageDog);
    console.log(dog);
    return (
      <div>
        { storageDog.map(({ message, status }) => {
          return (
            <div key={ message }>
              <img src={ message } alt={ status } />
            </div>
          ) }) }
      </div>
    );
  }
}

export default Dog;
