import React, { Component } from 'react';

class Dog extends Component {
  constructor(props) {
    super(props);

    this.fetchDog = this.fetchDog.bind(this);

    this.state = {
      loading: true,
      dog: undefined,
      storageDog: [],
    };
  }

  componentDidMount() {
    this.fetchDog();
  }

  async fetchDog() {
    const requestReturn = await fetch('https://dog.ceo/api/breeds/image/random');
    const requestObj = await requestReturn.json();
    this.setState({
      dog: requestObj,
    });
  }

  saveDogs() {
    this.setState(({ dog, storageDog }) => {
      storageDog: [...storageDog, dog]
    });
    this.fetchDog();
  }

  render() {
    const { dog, storageDog } = this.state;
    console.log(storageDog);
    console.log(dog);
    return (
      <div>
        <div>
          { storageDog.map(({ message }) => (
            <img src={ message } key={ message } alt={ message } />)) }
        </div>
      </div>
    );
  }
}

export default Dog;
