import React, { Component } from 'react';

class Dog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      dog: '',
      storageDog: [],
    };
  }

  async fetchDog() {
    const requestReturn = await fetch('https://dog.ceo/api/breeds/image/random');
    const requestObj = await requestReturn.json();
    this.setState({
      storageDog: requestObj,
    });
  }

  componentDidMount() {
    this.fetchDog();
  }

  render() {
    const { dog, storageDog } = this.state;
    return (
      <div>
        { storageDog.map(({message, status}) => (<span key={message}>{ message }</span>)) }
      </div>
    );
  }
}

export default Dog;
